ee:message.ee:set-payload:
%dw 2.0 output application/json skipNullOn="everywhere"
// Next functions mask sensitive data and are only temporal before production fun maskData(num: Any) = "*masked*" fun replaceInitialChars(str: String) = if(sizeOf(str) > 3) "***" ++ (str as String)[3 to -1] // Replace first 3 characters and append the rest else "***" // If less than 3 characters, just replace the whole string
--- payload map (item, value) ->{ contactId: item.ContactID as String, (firstName: item.PrimaryFirstName) if (!isEmpty(item.PrimaryFirstName)), (lastName: item.PrimaryLastName) if (!isEmpty(item.PrimaryLastName)), (middleInitial: item.PrimaryMiddleInitial) if (!isEmpty(item.PrimaryMiddleInitial)), name: item.ContactName, (gender: item.PrimaryGender) if (!isEmpty(item.PrimaryGender)), (ssn: item.SSN) if (!isEmpty(item.SSN)), ssnFlag: !isEmpty(item.SSNFlag) and item.SSNFlag != 0, (dob: item.DateOfBirth) if (!isEmpty(item.DateOfBirth)), (dod: item.DateOfDeath) if (!isEmpty(item.DateOfDeath)), activeFlag: !isEmpty(item.ActiveFlag) and item.ActiveFlag != 0, disbursementFlag:!isEmpty(item.DisbursementEligibilityFlag) and item.DisbursementEligibilityFlag != 0, date: item.ValidFrom }
